import React from 'react'
import { useState } from 'react';
import BlogList from './BlogList';

function Home() {

    const [blogs, setBlogs] = useState([
        { title: "L'amour de soi", body: "lorem ipsum...", author:"Hesper", id:1},
        { title: "D'anniversaire", body:"lorem ipsum...", author:"Hesper", id:2},
        { title: "Home", body:"lorem ipsum...", author:"Hesper", id:3},
        { title: "Borahe", body:"lorem ipsum...", author:"Vante", id:4}
    ]);

    const handleDelete = (id) => {
        const newBlogs = blogs.filter((blog) => blog.id !== id);
        setBlogs(newBlogs)
    }

    return (
        <div>
            <div className="home">
                <BlogList blogs={blogs} title={"All Blogs"} handleDelete={handleDelete} />
                <br />
                {/* <BlogList blogs={blogs.filter((blog) => blog.author === "Vante")} title={"Vante's Blog"} /> */}
            </div>
        </div>
    )
}

export default Home
