import React from 'react'
import { useParams } from 'react-router-dom';

function BlogDetails() {
    const { id } = useParams();
    const { data, error, isLoading } = useFetch(`http://localhost:8000/blogs/${id}`);  

    return (
        <div>
            <div className="blog-details">
                <h2>The Chaos</h2>
                <p>{ id }</p>
            </div>
        </div>
    )
}

export default BlogDetails
